import React from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import "./index.scss";
const Carousels = ({ images, id }) => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
  };

  const a = [
    {
      url: "https://res.cloudinary.com/cuto/image/upload/v1631986964/instagram-clone/vew4inuk494xzfhqyjkg.jpg",
    },
    {
      url: "https://res.cloudinary.com/cuto/image/upload/v1631986964/instagram-clone/vew4inuk494xzfhqyjkg.jpg",
    },
    {
      url: "https://res.cloudinary.com/cuto/image/upload/v1631986964/instagram-clone/vew4inuk494xzfhqyjkg.jpg",
    },
  ];

  const divImage = (img) => {
    return {
      backgroundImage: `url(${img})`,
      backgroundRepeat: "no-repeat",
      backgroundSize: "cover",
      width: "615px",
      height: "615px",
    };
  };

  return (
    <>
      <div className="carousels-container">
        {/* <Slider {...settings}>
          {images.map((img, index) => (
            <div className="carousels-container-img" key={index}>
              <img src={img.url} alt="Img" />
            </div>
          ))}
        </Slider> */}
        <div
          style={{
            backgroundImage:
              'url("https://images.pexels.com/photos/34153/pexels-photo.jpg?auto=compress&cs=tinysrgb&h=350")',
            backgroundPosition: "center",
            backgroundSize: "cover",
            backgroundRepeat: "no-repeat",
            width: "612px",
            height: "612px",
          }}
        ></div>
      </div>
    </>
  );
};

export default Carousels;
