import { PROFILE_TYPES } from "../types/profileTypes";

export const getProfileUsers = ({ users, id, auth }) => {
  return async (dispatch) => {
    if (users.every((user) => user._id !== id)) {
      try {
        dispatch({
          type: PROFILE_TYPES.LOADING,
          payload: true,
        });
        
      } catch (err) {}
    }
  };
};
