import React from "react";
import Comments from "../../../components/Comments";
import CardActions from "../../../components/PostCards/CardFooter/CardActions";
import moment from "moment";
import "./index.scss";
import InputComment from "../../../components/Comments/InputComment";
const PostActions = ({ post, handleCheck }) => {
  return (
    <>
      <div className="posts-footer">
        <div className="posts-footer-actions">
          <CardActions post={post} />
        </div>
        <div className="posts-footer-input">
          <div className="post-footer-likes">{post.likes.length} likes</div>
          <div className="post-footer-createdAt">
            <p>{moment(post.createdAt).fromNow()}</p>
          </div>
          <InputComment post={post} />
        </div>
      </div>
    </>
  );
};

export default PostActions;
