import React, { useEffect, useRef, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router";
import LoadingIcon from "../../assets/images/loading.gif";
import {
  addMessageAction,
  getMessagesAction,
} from "../../redux/actions/messageActions";
import { MESSAGE_TYPES } from "../../redux/types/messageTypes";
import { NOTIFY_TYPES } from "../../redux/types/notifyTypes";
import { imageUpload } from "../../utils/imageUpload";
import { imageShow, videoShow } from "../../utils/mediaShow";
import UserCard from "../UserCard";
import "./index.scss";
import MsgDisplay from "./MsgDisplay";
const RightSide = () => {
  const { auth, message, socket } = useSelector((state) => state);
  const dispatch = useDispatch();
  const { id } = useParams();
  const [user, setUser] = useState([]);
  const [text, setText] = useState("");
  const [media, setMedia] = useState([]);
  const [loadMedia, setLoadMedia] = useState(false);

  const refDisplay = useRef();
  const pageEnd = useRef();

  const [page, setPage] = useState(0);

  // const [data, setData] = useState([]);

  // useEffect(() => {
  //   const newData = message.data.filter(
  //     (item) => item.sender === auth.user._id || item.sender === id
  //   );
  //   setData(newData);
  //   setPage(1);
  // }, [message.data, id]);

  useEffect(() => {
    const newUser = message.users.find((user) => user._id === id);
    if (newUser) {
      setUser(newUser);
    }
  }, [message.users, id]);

  const handleChangeMedia = (e) => {
    const files = [...e.target.files];
    let err = "";
    let newMedia = [];
    files.forEach((file) => {
      if (!file) return (err = "File does not exist.");

      if (file.size > 1024 * 1024 * 5) {
        return (err = "This image/video larget is 5mb");
      }

      return newMedia.push(file);
    });

    if (err)
      dispatch({
        type: NOTIFY_TYPES.NOTIFY,
        payload: { error: err },
      });
    setMedia([...media, ...newMedia]);
  };

  const handleDeleteMedia = (index) => {
    const newArr = [...media];
    newArr.splice(index, 1);
    setMedia(newArr);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!text.trim() && media.length === 0) return;
    setText("");
    setMedia([]);
    setLoadMedia(true);
    let newArr = [];
    if (media.length > 0) newArr = await imageUpload(media);
    const msg = {
      sender: auth.user._id,
      recipient: id,
      text,
      media: newArr,
      createdAt: new Date().toISOString(),
    };
    setLoadMedia(false);
    await dispatch(addMessageAction({ msg, auth, socket }));
    if (refDisplay.current) {
      refDisplay.current.scrollIntoView({
        behavior: "smooth",
        block: "end",
      });
    }
  };

  useEffect(() => {
    if (id) {
      const getMessageData = async () => {
        dispatch({
          type: MESSAGE_TYPES.GET_MESSAGES,
          payload: { messages: [] },
        });
        setPage(1);
        await dispatch(getMessagesAction({ auth, id }));
        if (refDisplay.current) {
          refDisplay.current.scrollIntoView({
            behavior: "smooth",
            block: "end",
          });
        }
      };
      getMessageData();
    }
  }, [id, auth, dispatch]);

  //Load more

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        if (entries[0].isIntersecting) {
          setPage((p) => p + 1);
        }
      },
      {
        threshold: 0.1,
      }
    );

    observer.observe(pageEnd.current);
  }, [setPage]);

  useEffect(() => {
    if (message.resultData >= (page - 1) * 9 && page > 1) {
      dispatch(getMessagesAction({ auth, id, page }));
    }
  }, [message.resultData, auth, dispatch, id, page]);

  useEffect(() => {
    if (refDisplay.current) {
      refDisplay.current.scrollIntoView({
        behavior: "smooth",
        block: "end",
      });
    }
  }, [text]);

  return (
    <>
      <div className="message-header">
        <div className="message-heading">
          {user.length !== 0 && (
            <UserCard user={user} msg={true}>
              <i className="fas fa-info-circle message-icon"></i>
            </UserCard>
          )}
        </div>

        <div
          className="message-chat"
          style={{
            height:
              media.length > 0 ? "calc(100% - 186px)" : "calc(100% - 116px)",
          }}
        >
          <div className="message-chat-display" ref={refDisplay}>
            <button className="message-chat-btn" ref={pageEnd}>
              LoadMore
            </button>
            {message.data.map((msg, index) => (
              <div key={index}>
                {msg.sender !== auth.user._id && (
                  <div className="message-chat-row other-message">
                    <MsgDisplay user={user} msg={msg} />
                  </div>
                )}

                {msg.sender === auth.user._id && (
                  <div className="message-chat-row you-message">
                    <MsgDisplay user={auth.user} msg={msg} />
                  </div>
                )}
              </div>
            ))}

            {loadMedia && (
              <div className="message-chat-row you-message">
                <img src={LoadingIcon} alt="Loading" />
              </div>
            )}
          </div>
        </div>

        <div
          className="message-media"
          style={{ display: media.length > 0 ? "grid" : "none" }}
        >
          {media.map((item, index) => (
            <div key={index} className="message-media-list">
              {item.type.match(/video/i)
                ? videoShow(URL.createObjectURL(item))
                : imageShow(URL.createObjectURL(item))}
              <span
                className="message-media-close"
                onClick={() => handleDeleteMedia(index)}
              >
                &times;
              </span>
            </div>
          ))}
        </div>

        <form className="message-form" onSubmit={handleSubmit}>
          <input
            type="text"
            className="message-form-input"
            placeholder="Enter your message...."
            value={text}
            onChange={(e) => setText(e.target.value)}
          />

          <div className="message-form-upload">
            <i className="fas fa-image message-form-icon"></i>
            <input
              type="file"
              name="file"
              className="message-form-file"
              multiple
              accept="image/*,video/*"
              onChange={handleChangeMedia}
            />
          </div>

          <button
            type="submit"
            className="message-form-btn"
            // disabled={text || media.length > 0 ? false : true}
          >
            Send
          </button>
        </form>
      </div>
    </>
  );
};

export default RightSide;
