import React, { useState } from "react";
import { useLocation, useParams } from "react-router";
import Forgot from "../../assets/images/forgotPassword.svg";
import { Link } from "react-router-dom";
import AppStoreIcon from "../../assets/images/applestore.png";
import GoogleIcon from "../../assets/images/googlestore.png";
import { useDispatch } from "react-redux";
import { NOTIFY_TYPES } from "../../redux/types/notifyTypes";
import { resetPasswordAction } from "../../redux/actions/authActions";
const ResetPasswordPage = () => {
  const { token } = useParams();
  const initialState = {
    password: "",
    cf_password: "",
  };
  const [data, setData] = useState(initialState);
  const dispatch = useDispatch();
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setData({ ...data, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (data.password !== data.cf_password) {
      return dispatch({
        type: NOTIFY_TYPES.NOTIFY,
        payload: { error: "Your password doesn't match with confirm password" },
      });
    }

    dispatch(resetPasswordAction({ password: data.password, token }));
  };

  return (
    <>
      <div className="forgot-container">
        <div className="forgot-content">
          <div className="forgot-images">
            <img src={Forgot} alt="forgot" />
          </div>
          <div className="forgot-input">
            <form
              action="#"
              onSubmit={handleSubmit}
              className="forgot-input-form"
            >
              <h1 className="forgot-input-heading">Reset your password</h1>'
              <input
                type="password"
                name="password"
                value={data.password}
                onChange={handleInputChange}
                placeholder="Enter your password...."
              />
              <input
                type="password"
                name="cf_password"
                value={data.cf_password}
                onChange={handleInputChange}
                className="forgot-input-cfpassword"
                placeholder="Confirm your password...."
              />
              <button
                className="forgot-input-btn"
                disabled={data.password && data.cf_password ? false : true}
                style={{
                  opacity: data.password && data.cf_password ? 1 : 0.3,
                }}
              >
                Submit
              </button>
            </form>

            <div className="instagram-apps">
              <p className="instagram-apps-heading">Get the app</p>
              <div className="instagram-apps-download">
                <img src={AppStoreIcon} alt="AppStore" />
                <img src={GoogleIcon} alt="GoogleStore" />
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default ResetPasswordPage;
