import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router";
import Icons from "../Icons";
import UserCard from "../UserCard";
import "./index.scss";
import MsgDisplay from "./MsgDisplay";
const RightSide = () => {
  const { auth, message } = useSelector((state) => state);
  const dispatch = useDispatch();
  const { id } = useParams();
  const [user, setUser] = useState([]);
  const [text, setText] = useState("");
  useEffect(() => {
    const newUser = message.users.find((user) => user._id === id);
    if (newUser) {
      setUser(newUser);
    }
  }, [message.users, id]);


  const handleChangeMedia = e => {
    
  }


  return (
    <>
      <div className="message-header">
        <div className="message-heading">
          <UserCard user={user}>
            <i className="fas fa-trash message-icon"></i>
          </UserCard>
        </div>

        <div className="message-chat">
          <div className="message-chat-display">
            <div className="message-chat-row other-message">
              <MsgDisplay user={user} />
            </div>

            <div className="message-chat-row you-message">
              <MsgDisplay user={auth.user} />
            </div>
          </div>
        </div>

        <form className="message-form">
          <input
            type="text"
            className="message-form-input"
            placeholder="Enter your message...."
            value={text}
            onChange={(e) => setText(e.target.value)}
          />

          <div className="message-form-upload">
            <i className="fas fa-image message-form-icon"></i>
            <input
              type="file"
              name="file"
              id="file"
              multiple
              accept="image/*,video/*"
              onChange={handleChangeMedia}
            />
          </div>

          <div className="message-icons">
            <Icons setContent={setText} content={text} />
          </div>

          <button
            className="message-form-btn"
            disabled={text ? false : true}
            type="submit"
          >
            Send
          </button>
        </form>
      </div>
    </>
  );
};

export default RightSide;
