import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { createCommentAction } from "../../../redux/actions/commentActions";
import { STATUS_TYPES } from "../../../redux/types/statusTypes";
import Icons from "../../Icons";
import "./index.scss";
const InputComment = ({ children, post, onReply, setOnReply }) => {
  const { auth, socket, input } = useSelector((state) => state);
  const [content, setContent] = useState("");
  const dispatch = useDispatch();

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!content.trim()) {
      if (setOnReply) return setOnReply(false);
      return;
    }

    const newComment = {
      content,
      likes: [],
      user: auth.user,
      createdAt: new Date().toISOString(),
      reply: onReply && onReply.commentId,
      tag: onReply && onReply.user,
    };

    const newComments = {
      content,
      likes: [],
      user: auth.user,
      createdAt: new Date().toISOString(),
      reply: undefined,
      tag: undefined,
    };
    const replyComments = {
      content,
      likes: [],
      user: auth.user,
      createdAt: new Date().toISOString(),
      reply: input.commentId,
      tag: input.user,
    };

    if (input.isReply) {
      console.log(replyComments);
    } else {
      console.log(newComments);
    }

    dispatch({
      type: STATUS_TYPES.ON_REPLY,
      payload: false,
    });
    // dispatch(createCommentAction({ post, newComment, auth, socket }));
    if (setOnReply) return setOnReply(false);

    setContent("");
  };

  return (
    <>
      <div className="input-comments">
        <form className="input-form" onSubmit={handleSubmit}>
          {children}
          
          <input
            type="text"
            className="input-comment"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            placeholder="Add a comment..."
          />

          <div className="input-icons">
            <Icons setContent={setContent} content={content} />
          </div>

          <button
            type="submit"
            className="input-btn"
            disabled={content ? false : true}
            style={{ opacity: content ? 1 : 0.5 }}
          >
            Post
          </button>
        </form>
      </div>
    </>
  );
};

export default InputComment;
