import React, { useEffect, useState } from "react";
import PostThumb from "../../components/PostProfile/PostThumb";
import LoadingIcon from "../../assets/images/loading.gif";
import "./index.scss";
import LoadMoreBtn from "../../components/LoadMoreBtn";
const PostProfiles = ({ auth, id, dispatch, profile }) => {
  const [posts, setPosts] = useState([]);
  const [result, setResult] = useState(9);
  const [page, setPage] = useState(0);
  const [load, setLoad] = useState(false);

  useEffect(() => {
    profile.posts.forEach((data) => {
      if (data._id === id) {
        setPosts(data.posts);
        setResult(data.result);
      }
    });
  }, [profile.posts, id]);

  const handleLoadMore = () => {
    
  }

  return (
    <>
      <div className="post-profile-container">
        <PostThumb posts={posts} result={result} />
        {load && (
          <div className="discover-page-loading">
            <img src={LoadingIcon} alt="Logo" />
          </div>
        )}
        <div className="discover-page-btn">
          <LoadMoreBtn
            result={result}
            page={page}
            load={load}
            handleLoadMore={handleLoadMore}
          />
        </div>
      </div>
    </>
  );
};

export default PostProfiles;
