import React from "react";
import CommentDisplay from "./CommentDisplay";
import "./index.scss";
const Comments = ({ post }) => {
  return (
    <>
      <div className="comment-container">
        {post.comments.map((comment) => (
          <CommentDisplay key={comment._id} comment={comment} post={post} />
        ))}
      </div>
    </>
  );
};

export default Comments;
