import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { useLocation } from "react-router";
import { ReactComponent as Explore } from "../../../assets/images/explore.svg";
import { ReactComponent as Home } from "../../../assets/images/home.svg";
import { ReactComponent as Inbox } from "../../../assets/images/inbox.svg";
import { ReactComponent as Notifications } from "../../../assets/images/notifications.svg";
import "./index.scss";
const Menu = () => {
  const navLinks = [
    {
      label: "Home",
      icon: "home",
      path: "/",
    },
    {
      label: "Message",
      icon: "near_me",
      path: "/message",
    },
    {
      label: "Discover",
      icon: "explore",
      path: "/discover",
    },
  ];

  const { auth, notify } = useSelector((state) => state);
  const dispatch = useDispatch();

  const { pathname } = useLocation();
  const isActive = (pn) => {
    if (pn === pathname) return "active";
  };

  
  return (
    <>
      <div className="menu-container">
        <Home className="menu-icons" />
        <Inbox className="menu-icons" />
        <Explore className="menu-icons" />
        <Notifications className="menu-icons" />
      </div>
    </>
  );
};

export default Menu;
