import React, { useState } from "react";
import "./index.scss";
import { useDispatch } from "react-redux";
import { MODAL_TYPES } from "../../../../../../redux/types/modalTypes";

const CommentMenu = ({ post, comment, auth, setOnEdit }) => {
  const [isShow, setIsShow] = useState(false);
  const dispatch = useDispatch();

  const handleShowActions = () => {
    setIsShow(true);
    dispatch({
      type: MODAL_TYPES.MODAL,
      payload: true,
    });
  };

  const handleOnEdit = () => {
    setOnEdit(true);
    setIsShow(false);
    dispatch({
      type: MODAL_TYPES.MODAL,
      payload: false,
    });
  };

  const handleUnShowActions = () => {
    setIsShow(false);
    dispatch({
      type: MODAL_TYPES.MODAL,
      payload: false,
    });
  };

  return (
    <>
      <div className="comment-menu">
        {(post.user._id === auth.user._id ||
          comment.user._id === auth.user._id) && (
          <div className="comment-menu-dropdown">
            <span className="material-icons dark" onClick={handleShowActions}>
              more_vert
            </span>
            {isShow && (
              <>
                <div className="comment-menu-modal">
                  <div className="comment-menu-list">
                    <div className="comment-menu-items">
                      <span
                        className="comment-menu-actions red"
                        onClick={handleOnEdit}
                      >
                        Edit
                      </span>
                    </div>
                    <div className="comment-menu-items">
                      <span className="comment-menu-actions red">Delete</span>
                    </div>
                    <div className="comment-menu-items">
                      <span
                        className="comment-menu-actions"
                        onClick={handleUnShowActions}
                      >
                        Cancel
                      </span>
                    </div>
                  </div>
                </div>
              </>
            )}
          </div>
        )}
      </div>
    </>
  );
};

export default CommentMenu;
