import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router";
import "./index.scss";
import Forgot from "../../assets/images/forgotPassword.svg";
import { Link } from "react-router-dom";
import AppStoreIcon from "../../assets/images/applestore.png";
import GoogleIcon from "../../assets/images/googlestore.png";
import { forgotPasswordAction } from "../../redux/actions/authActions";
const ForgotPasswordPage = () => {
  const { auth } = useSelector((state) => state);
  const [inputValue, setInputValue] = useState("");
  const dispatch = useDispatch();
  const history = useHistory();

  useEffect(() => {
    if (!auth.user) {
      history.push("/forgotPassword");
    } else {
      history.push("/");
    }
  }, [auth, history]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (inputValue.trim() === "") return;
    dispatch(forgotPasswordAction(inputValue));
    setInputValue("");
  };

  return (
    <>
      <div className="forgot-container">
        <div className="forgot-content">
          <div className="forgot-input">
            <form
              action="#"
              onSubmit={handleSubmit}
              className="forgot-input-form"
            >
              <h1 className="forgot-input-heading">Enter your email</h1>'
              <input
                type="email"
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
                placeholder="Enter your email...."
              />
              <button className="forgot-input-btn">Send email</button>
            </form>

            <div className="forgot-account">
              <p className="forgot-not-account">Don't have an account ? </p>
              <p className="forgot-not-account">
                <Link to="/signup"> Sign up</Link>
              </p>
            </div>

            <div className="instagram-apps">
              <p className="instagram-apps-heading">Get the app</p>
              <div className="instagram-apps-download">
                <img src={AppStoreIcon} alt="AppStore" />
                <img src={GoogleIcon} alt="GoogleStore" />
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default ForgotPasswordPage;
