import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDiscoverPosts } from "../../redux/actions/discoverActions";
import LoadingIcon from "../../assets/images/loading.gif";
import "./index.scss";
import PostThumb from "../../components/PostProfile/PostThumb";
const DiscoverPage = () => {
  const { auth, discover } = useSelector((state) => state);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getDiscoverPosts(auth.token));
  }, [dispatch, auth.token]);

  return (
    <>
      <div className="discover-page">
        <div className="discover-page-container">
          {discover.loading ? (
            <>
              <div className="discover-page-loading">
                <img src={LoadingIcon} alt="Logo" />
              </div>{" "}
            </>
          ) : (
            <>
              <div className="discover-page-details">
                <PostThumb posts={discover.posts} result={discover.result} />
              </div>
            </>
          )}
        </div>
      </div>
    </>
  );
};

export default DiscoverPage;
