import { MESSAGE_TYPES } from "../types/messageTypes";

export const addUserAction = ({ user, message }) => {
  return async (dispatch) => {
    if (message.users.every((item) => item._id !== user._id)) {
      dispatch({
        type: MESSAGE_TYPES.ADD_USER,
        payload: user,
      });
    }
  };
};
