import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Route, Switch } from "react-router-dom";
import "./App.scss";
import Header from "./components/Header";
import Notify from "./components/Notify";
import PrivateRouter from "./customHook/PrivateRouter";
import HomePage from "./pages/HomePage";
import LoginPage from "./pages/LoginPage";
import ProfilePage from "./pages/ProfilePage";
import RegisterPage from "./pages/RegisterPage";
import { refreshToken } from "./redux/actions/authActions";
import DiscoverPage from "./pages/DiscoverPage";
import MessagePage from "./pages/MessagePage";
function App() {
  const { auth } = useSelector((state) => state);

  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(refreshToken());
  }, [dispatch]);

  return (
    <>
      <div className="App">
        <Notify />
        <Switch>
          {auth.token && <Header />}
          <Route exact path="/" component={auth.token ? HomePage : LoginPage} />
          <Route path="/signup" component={RegisterPage} />
          <Route path="/asd">
            <h3>Please select a topic.</h3>
          </Route>
          <PrivateRouter exact path="/profile/:id" component={ProfilePage} />
          <PrivateRouter path="/discover" component={DiscoverPage} />
          <PrivateRouter path="/message" component={MessagePage} />
          {/* <Route exact="*" component={PageNotFound} /> */}
        </Switch>
      </div>
    </>
  );
}

export default App;
