import React, { useState } from "react";
import { useSelector } from "react-redux";
import EditActions from "../../components/EditActions";
import "./index.scss";
import Profile from "../../assets/images/profile.jpg";
const EditPage = () => {
  const { auth } = useSelector((state) => state);
  const initialState = {
    fullname: "",
    mobile: "",
    address: "",
    website: "",
    story: "",
    gender: "",
  };

  const [userData, setUserData] = useState(initialState);
  const { fullname, mobile, address, website, story, gender } = userData;
  const [avatar, setAvatar] = useState("");

  const changeAvatar = (e) => {};

  const handleInput = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <>
      <div className="edit">
        <div className="edit-contanier">
          <div className="edit-leftside">
            <EditActions />
          </div>
          <div className="edit-rightside">
            <form action="#" className="edit-rightside-form">
              <div className="edit-rightside-heading">
                <div className="edit-rightside-width">
                  <img
                    src={
                      avatar ? URL.createObjectURL(avatar) : auth.user.avatar
                    }
                    alt="Profile"
                  />
                </div>
                <div className="edit-rightside-name">
                  <h1 className="edit-rightside-username">
                    {auth.user.username}
                  </h1>
                  <div style={{ cursor: "pointer" }}>
                    <span className="edit-rightside-btn">
                      Change Profile Picture
                    </span>
                    <input
                      className="edit-rightside-file"
                      type="file"
                      name="file"
                      id="file_up"
                      accept="image/*"
                      onChange={changeAvatar}
                    />
                  </div>
                </div>
              </div>
              <div className="edit-rightside-input">
                <div className="edit-rightside-group">
                  <div className="edit-rightside-width">
                    <label htmlFor="">Name</label>
                  </div>
                  <div>
                    <input
                      type="text"
                      className="form-input"
                      name="fullname"
                      value={fullname}
                      onChange={handleInput}
                    />
                  </div>
                </div>
                <div className="edit-rightside-group">
                  <div className="edit-rightside-width">
                    <label htmlFor="">Name</label>
                  </div>
                  <div>
                    <input
                      type="text"
                      className="form-input"
                      name="fullname"
                      value={fullname}
                      onChange={handleInput}
                    />
                  </div>
                </div>
                <div className="edit-rightside-group">
                  <div className="edit-rightside-width">
                    <label htmlFor="">Name</label>
                  </div>
                  <div>
                    <input
                      type="text"
                      className="form-input"
                      name="fullname"
                      value={fullname}
                      onChange={handleInput}
                    />
                  </div>
                </div>
                <div className="edit-rightside-group">
                  <div className="edit-rightside-width">
                    <label htmlFor="">Name</label>
                  </div>
                  <div>
                    <input
                      type="text"
                      className="form-input"
                      name="fullname"
                      value={fullname}
                      onChange={handleInput}
                    />
                  </div>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};

export default EditPage;
