import React from "react";
import { Link } from "react-router-dom";
import ProfileIcon from "../../ProfileIcon";
import "./index.scss";
import moment from "moment";
import { useSelector } from "react-redux";
const CardHeader = ({ post }) => {
  const { auth } = useSelector((state) => state);
  return (
    <>
      <div className="card-header">
        <div className="card-header-content">
          <div className="card-header-img">
            <ProfileIcon src={post.user.avatar} size="big-avatar" />
          </div>

          <div className="card-header-name">
            <h6>
              <Link to={`/profile/${post.user._id}`}>{post.user.username}</Link>
            </h6>
            <small>{moment(post.createdAt).fromNow()}</small>
          </div>

          {/* <div className="card-header-drowndown">
            <span className="material-icons">more_horiz</span>

            <div className="card-header-menu">
              {auth.user._id === post.user._id && (
                <>
                  <div className="card-header-items">
                    <span className="material-icons">create</span> Edit Post
                  </div>
                  <div className="card-header-items">
                    <span className="material-icons">delete-outline</span> Remove Post
                  </div>
                </>
              )}
              <div className="card-header-items">
                <span className="material-icons">content_copy</span> Copy Link
              </div>
            </div>
          </div> */}
        </div>
      </div>
    </>
  );
};

export default CardHeader;
