import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import moment from "moment";
import "./index.scss";
const CommentCard = ({ comment, post }) => {
  const [content, setContent] = useState("");
  const [readMore, setReadMore] = useState(false);

  useEffect(() => {
    setContent(comment.content);
  }, [comment.content]);

  return (
    <div className="comment-card">
      <div className="comment-card-container">
        <div className="comment-card-left">
          <Link
            className="comment-card-author"
            to={`/profile/${comment.user._id}`}
          >
            <span>{comment.user.username}</span>
          </Link>
          <div className="comment-card-details">
            <div className="comment-card-content">
              <div className="comment-card-text">
                <span>
                  {content.length < 100
                    ? content
                    : readMore
                    ? content + " "
                    : content.slice(0, 30) + "...."}
                </span>
                {content.length > 100 && (
                  <span
                    className="comment-card-readMore"
                    onClick={() => setReadMore(!readMore)}
                  >
                    {readMore ? "Hide content" : "Read More"}
                  </span>
                )}
              </div>
              <div className="comment-card-actions">
                <small>{moment(comment.createdAt).fromNow()}</small>
                <small className="bold">Reply</small>
              </div>
            </div>
          </div>
        </div>

        <div className="coment-card-like">
          <i className="far fa-heart small" style={{ fontSize: "20px" }}></i>
        </div>
      </div>
    </div>
  );
};

export default CommentCard;
