import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { useLocation } from "react-router";
import { Link } from "react-router-dom";
import { ReactComponent as Explore } from "../../../assets/images/explore.svg";
import { ReactComponent as Home } from "../../../assets/images/home.svg";
import { ReactComponent as Inbox } from "../../../assets/images/inbox.svg";
import { ReactComponent as Notifications } from "../../../assets/images/notifications.svg";
import ProfileIcon from "../../ProfileIcon";
import "./index.scss";
const Menu = () => {
  const navLinks = [
    {
      label: "Home",
      icon: "home",
      path: "/",
    },
    {
      label: "Message",
      icon: "near_me",
      path: "/message",
    },
    {
      label: "Discover",
      icon: "explore",
      path: "/discover",
    },
  ];

  const { auth, notify } = useSelector((state) => state);
  const [isShowNoti, setIsShowNoti] = useState(false);
  const [isShowActions, setIsShowActions] = useState(false);
  const dispatch = useDispatch();

  const { pathname } = useLocation();
  const isActive = (pn) => {
    if (pn === pathname) return "active";
  };

  return (
    <>
      <div className="menu-container">
        <ul className="menu-navbar">
          {navLinks.map((link, index) => (
            <li
              className={`menu-navbar-items ${isActive(link.path)}`}
              key={index}
            >
              <Link className="menu-navbar-link" to={link.path}>
                <span className="material-icons">{link.icon}</span>
              </Link>
            </li>
          ))}

          <li
            className={`menu-navbar-items menu-navbar-dropdown ${
              isShowNoti ? "show" : ""
            }`}
            onClick={() => setIsShowNoti(!isShowNoti)}
            style={{ opacity: 1 }}
          >
            <span className="material-icons">favorite</span>
          </li>

          <li
            className={`menu-navbar-items menu-navbar-dropdown ${
              isShowActions ? "show" : ""
            }`}
            onClick={() => setIsShowActions(!isShowActions)}
            style={{ opacity: 1 }}
          >
            <span id="navbarDropdown">
              <ProfileIcon src={auth.user.avatar} size="medium-avatar" />
            </span>
            <div className="dropdown-menu">
              <Link className="dropdown-items" to={`/profile/${auth.user._id}`}>
                Profile
              </Link>
              <Link className="dropdown-items" to="/">
                Logout
              </Link>
            </div>
          </li>
        </ul>
      </div>
    </>
  );
};

export default Menu;
