import { getDataAPI, patchDataAPI } from "../../utils/fetchData";
import { imageUpload } from "../../utils/imageUpload";
import { AUTH_TYPES } from "../types/authTypes";
import { NOTIFY_TYPES } from "../types/notifyTypes";
import { PROFILE_TYPES } from "../types/profileTypes";

export const getProfileUsers = ({ users, id, auth }) => {
  return async (dispatch) => {
    if (users.every((user) => user._id !== id)) {
      try {
        dispatch({
          type: PROFILE_TYPES.LOADING,
          payload: true,
        });
        const res = await getDataAPI(`/user/${id}`, auth.token);
        dispatch({
          type: PROFILE_TYPES.GET_USER,
          payload: res.data,
        });
        dispatch({
          type: PROFILE_TYPES.LOADING,
          payload: false,
        });
      } catch (err) {
        dispatch({
          type: NOTIFY_TYPES.NOTIFY,
          payload: { error: err.response.data.msg },
        });
      }
    }
  };
};

export const updateProfileUser =
  ({ userData, avatar, auth }) =>
  async (dispatch) => {
    if (!userData.fullname)
      return dispatch({
        type: NOTIFY_TYPES.NOTIFY,
        payload: { error: "Please add your full name" },
      });
    if (userData.fullname.length > 25)
      return dispatch({
        type: NOTIFY_TYPES.NOTIFY,
        payload: { error: "Your full name is too long" },
      });
    if (userData.story.length > 200)
      return dispatch({
        type: NOTIFY_TYPES.NOTIFY,
        payload: { error: "Your story is too long" },
      });
    try {
      let media;
      dispatch({
        type: NOTIFY_TYPES.NOTIFY,
        payload: { loading: true },
      });
      if (avatar) media = await imageUpload([avatar]);

      const res = await patchDataAPI(
        "user",
        {
          ...userData,
          avatar: avatar ? media[0].url : auth.user.avatar,
        },
        auth.token
      );

      dispatch({
        type: AUTH_TYPES.AUTH,
        payload: {
          ...auth,
          user: {
            ...auth.user,
            ...userData,
            avatar: avatar ? media[0].url : auth.user.avatar,
          },
        },
      });

      dispatch({
        type: NOTIFY_TYPES.NOTIFY,
        payload: { loading: false },
      });
    } catch (err) {
      dispatch({
        type: NOTIFY_TYPES.NOTIFY,
        payload: { error: err.response.data.msg },
      });
    }
  };
