import React, { useState } from "react";
import { useSelector } from "react-redux";
import EditActions from "../../components/EditActions";

const ChangePasswordPage = () => {
  const { auth } = useSelector((state) => state);

  const initialState = {
    oldPassword: "",
    newPassword: "",
    cfPassword: "",
  };

  const [userData, setUserData] = useState(initialState);
  const { oldPassword, newPassword, cfPassword } = userData;
  const handleInput = (e) => {
    const { name, value } = e.target;
    setUserData({ ...userData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // dispatch(updateProfileUser({ userData, avatar, auth }));
  };
  return (
    <>
      <div className="edit">
        <div className="edit-contanier">
          <div className="edit-leftside">
            <EditActions />
          </div>
          <div className="edit-rightside">
            <form
              action="#"
              className="edit-rightside-form"
              onSubmit={handleSubmit}
            >
              <div className="edit-rightside-heading">
                <div className="edit-rightside-width">
                  <img src={auth.user.avatar} alt="Profile" />
                </div>
                <div className="edit-rightside-name">
                  <h1 className="edit-rightside-username">
                    {auth.user.username}
                  </h1>
                </div>
              </div>

              <div className="edit-rightside-input">
                <div className="edit-rightside-group">
                  <div className="edit-rightside-width">
                    <label htmlFor="">Old Password</label>
                  </div>
                  <div>
                    <input
                      type="text"
                      className="form-input"
                      name="oldPassword"
                      value={oldPassword}
                      onChange={handleInput}
                    />
                  </div>
                </div>
              </div>

              <div className="edit-rightside-input">
                <div className="edit-rightside-group">
                  <div className="edit-rightside-width">
                    <label htmlFor="">New Password</label>
                  </div>
                  <div>
                    <input
                      type="text"
                      className="form-input"
                      name="newPassword"
                      value={newPassword}
                      onChange={handleInput}
                    />
                  </div>
                </div>
              </div>

              <div className="edit-rightside-input">
                <div className="edit-rightside-group">
                  <div className="edit-rightside-width">
                    <label htmlFor="">Confirm New Password</label>
                  </div>
                  <div>
                    <input
                      type="text"
                      className="form-input"
                      name="cfPassword"
                      value={cfPassword}
                      onChange={handleInput}
                    />
                  </div>
                </div>
              </div>

              <div className="edit-rightside-input center">
                <button className="edit-rightside-submit">Submit</button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
};

export default ChangePasswordPage;
