import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import moment from "moment";
import "./index.scss";
import LikeButton from "../../../../../LikeButton";
import CommentMenu from "../CommentMenu";
import { useSelector } from "react-redux";
const CommentCard = ({ comment, post }) => {
  const { auth } = useSelector((state) => state);
  const [content, setContent] = useState("");
  const [readMore, setReadMore] = useState(false);

  const [isLike, setIsLike] = useState(false);

  useEffect(() => {
    setContent(comment.content);
  }, [comment.content]);

  const handleLike = () => {};

  const handleUnlike = () => {};

  const styleCard = {
    opacity: comment._id ? 1 : 0.5,
    pointerEvents: comment._id ? "inherit" : "none",
  };

  return (
    <div className="comment-card" style={styleCard}>
      <div className="comment-card-container">
        <div className="comment-card-left">
          <Link
            className="comment-card-author"
            to={`/profile/${comment.user._id}`}
          >
            <span>{comment.user.username}</span>
          </Link>
          <div className="comment-card-details">
            <div className="comment-card-content">
              <div className="comment-card-text">
                <span>
                  {content.length < 100
                    ? content
                    : readMore
                    ? content + " "
                    : content.slice(0, 30) + "...."}
                </span>
                {content.length > 100 && (
                  <span
                    className="comment-card-readMore"
                    onClick={() => setReadMore(!readMore)}
                  >
                    {readMore ? "Hide content" : "Read More"}
                  </span>
                )}
              </div>
              <div className="comment-card-actions">
                <small>{moment(comment.createdAt).fromNow()}</small>
                <small className="bold">Reply</small>
              </div>
            </div>
          </div>
        </div>

        <div className="comment-card-likes">
          <CommentMenu post={post} comment={comment} auth={auth} />
          <LikeButton
            isLike={isLike}
            handleLike={handleLike}
            handleUnlike={handleUnlike}
            details={true}
          />
        </div>
      </div>
    </div>
  );
};

export default CommentCard;
