import React, { useState } from "react";
import "./index.scss";
const FollowBtn = ({ profile, user }) => {
  const [followed, setFollowed] = useState(false);
  return (
    <>
      {followed ? (
        <button
          className={
            profile
              ? "follow-btn follow-btn-user "
              : "follow-btn follow-btn-suggestion"
          }
        >
          UnFollow
        </button>
      ) : (
        <button
          className={
            profile
              ? "follow-btn follow-btn-user"
              : "follow-btn follow-btn-suggestion"
          }
        >
          Follow
        </button>
      )}
    </>
  );
};

export default FollowBtn;
