import { POST_TYPES } from "../types/postTypes";

const detailPostReducer = (state = [], action) => {
  switch (action.type) {
    case POST_TYPES.GET_POST:
      return [...state, action.payload];
    default:
      return state;
  }
};

export default detailPostReducer;
