import { postDataAPI } from "../../utils/fetchData";
import { NOTIFY_TYPES } from "../types/notifyTypes";
import { POST_TYPES } from "../types/postTypes";
import { editData } from "../types/globalTypes";
export const createCommentAction = ({ post, newComment, auth }) => {
  return async (dispatch) => {
    const newPost = { ...post, comments: [...post.comments, newComment] };
    dispatch({
      type: POST_TYPES.UPDATE_POST,
      payload: newPost,
    });

    try {
      const data = { ...newComment, postId: post._id };
      const res = await postDataAPI("comment", data, auth.token);
      const newData = {
        ...res.data.newComment,
        user: auth.user,
      };
      const newPost = { ...post, comments: [...post.comments, newData] };
      dispatch({
        type: POST_TYPES.UPDATE_POST,
        payload: newPost,
      });
    } catch (err) {
      dispatch({
        type: NOTIFY_TYPES.NOTIFY,
        payload: { error: err.response.data.msg },
      });
    }
  };
};

export const updateCommentAction = ({ comment, post, content, auth }) => {
  return async (dispatch) => {
    const newComments = editData(post.comments, comment._id, {
      ...comment,
      content,
    });

    const newPost = { ...post, comments: newComments };

    console.log(newPost);
  };
};
