import { postDataAPI } from "../../utils/fetchData";
import { NOTIFY_TYPES } from "../types/notifyTypes";
import { POST_TYPES } from "../types/postTypes";

export const createCommentAction = ({ post, newComment, auth }) => {
  return async (dispatch) => {
    const newPost = { ...post, comments: [...post.comments, newComment] };
    dispatch({
      type: POST_TYPES.UPDATE_POST,
      payload: newPost,
    });

    try {
      const data = { ...newComment, postId: post._id };
      const res = postDataAPI("comment", data, auth.token);

      console.log(res);
    } catch (err) {
      dispatch({
        type: NOTIFY_TYPES.NOTIFY,
        payload: { error: err.response.data.msg },
      });
    }
  };
};
